import React from 'react';
import 'react-vis/dist/style.css';
import '../css/main.css';
import {
  XYPlot,
  XAxis,
  YAxis,
  HorizontalGridLines,
  VerticalGridLines,
  VerticalBarSeries,
  DiscreteColorLegend
} from 'react-vis';

const ITEMS = [
  {title: 'Количество визитов в час', color: '#3fa6e9', strokeWidth: 14}
];

function DayChart(props) {
    var widthChart = Math.round(document.body.clientWidth * 0.80);
    return (
        <div>                
            <XYPlot xType="ordinal" width={widthChart} height={300}>
                <HorizontalGridLines/>
                <VerticalGridLines />
                <XAxis tickLabelAngle={0} />
                <YAxis />
                <VerticalBarSeries
                    data={props.chartDay}
                    color="#3fa6e9"
                />
            </XYPlot>
            <DiscreteColorLegend className="chart__legend" width={200} items={ITEMS} />
        </div>
    );
}

export default DayChart;
