import React from 'react';
import '../css/main.css';

class Userbar extends React.Component {
    constructor(props) {
        super(props);
        this.setDate = this.setDate.bind(this);
    }

    setDate(event) {
        var newDate = event.target.value;
        this.props.setDate(newDate);
    }

    render() {
        return (
            <div className="userbar">
                {this.props.children}
                    <input type="date" className="userbar__date" onChange={this.setDate} />
                <div className="userbar__info">
                    <h3>Зайцев Максим Олегович</h3>
                    <h4>Газпром Информ</h4>
                </div>
                <input className="userbar__logout" type="button" value="Выйти" />
            </div>
        );
    }
}

export default Userbar;
