import React from 'react';
import dataLoad from '../models/dataLoad';
import Navigation from './React.nav';
import Loader from './React.loader';
import moment from 'moment'
import MonthWorkspace from './React.month.workspace';
import '../css/main.css';

class Month extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            charts: {},
            defaultChart: [],
            strainData: [],
            startDate: '',
            finishDate: '',
            loading: false
        };
    }

    componentDidMount() {
        this.loadData();
    }

    loadData() {
        var urlLogs = 'http://localhost:3000/logs';

        var usersLogs;

        dataLoad(urlLogs).then(logs => {
            usersLogs = JSON.parse(logs);

            var usersChart = this.sortUsersChart(usersLogs);
            var visitsChart = this.sortVisitsChart(usersLogs);
            var viewsChart = this.sortViewsChart(usersLogs);
            var defaultChart = this.getDefaultChart();

            var charts = {
                users: {
                    chartId: 4501,
                    data: usersChart,
                    display: true,
                    color: '#84c9f6'
                },
                visits: {
                    chartId: 4502,
                    data: visitsChart,
                    display: true,
                    color: '#3fa6e9'
                },
                views: {
                    chartId: 4503,
                    data: viewsChart,
                    display: true,
                    color: '#127bbf'
                }
            };

            var finishDate = moment(new Date(), 'DD.MM').subtract(1, "days");
            var startDate = moment(new Date(), 'DD.MM').subtract(30, "days");

            var strainData = [];

            for (let i = 0; i < 30; i++) {
                var dayData = {
                    date: charts.users.data[i].x,
                    users: charts.users.data[i].y,
                    visits: charts.visits.data[i].y,
                    views: charts.views.data[i].y,
                }

                strainData.push(dayData);
            }

            this.setState({
                charts: charts,
                defaultChart: defaultChart,
                strainData: strainData,
                startDate: startDate.format('DD.MM'),
                finishDate: finishDate.format('DD.MM'),
                loading: true
            });

        }).catch(function(logs) {
            console.log('error ' + logs);
        })
    }

    sortVisitsChart(userLogs) {
        var currentlyDate = moment(new Date(), 'DD.MM');
        var startDate = currentlyDate.subtract(30, "days");
        var sortVisits = [];

        for (let i = 30; i > 0; i--) {
            var visitsOnDay = 0;
            for (let j = 0; j < userLogs.length; j++) {
                if (userLogs[j].time.slice(0, 5) === startDate.format('DD.MM')) {
                    visitsOnDay++;
                }
            }

            var oneDayValue = {
                x: startDate.format('DD.MM'),
                y: visitsOnDay
            };

            startDate = startDate.add(1, "days");
            sortVisits.push(oneDayValue);
        }

        return sortVisits;
    }

    sortViewsChart(userLogs) {
        var currentlyDate = moment(new Date(), 'DD.MM');
        var startDate = currentlyDate.subtract(30, "days");
        var sortViews = [];

        for (let i = 30; i > 0; i--) {
            var viewsOnDay = 0;
            for (let j = 0; j < userLogs.length; j++) {
                if (userLogs[j].time.slice(0, 5) === startDate.format('DD.MM')) {
                    viewsOnDay = viewsOnDay + userLogs[j].pages;
                }
            }

            var oneDayValue = {
                x: startDate.format('DD.MM'),
                y: viewsOnDay
            };

            startDate = startDate.add(1, "days");
            sortViews.push(oneDayValue);
        }

        return sortViews;
    }

    sortUsersChart(userLogs) {
        var currentlyDate = moment(new Date(), 'DD.MM');
        var startDate = currentlyDate.subtract(30, "days");
        var sortUsers = [];

        for (let i = 30; i > 0; i--) {
            var uniqueUsers = [];

            for (let j = 0; j < userLogs.length; j++) {
                if ((userLogs[j].date.slice(0, 5) === startDate.format('DD.MM')) && (!uniqueUsers.includes(userLogs[j].userId))){
                    uniqueUsers.push(userLogs[j].userId);
                }
            }

            var oneDay = {
                x: startDate.format('DD.MM'),
                y: uniqueUsers.length
            };

            startDate = startDate.add(1, "days");
            sortUsers.push(oneDay);
        }

        return sortUsers;
    }

    getDefaultChart() {
        var currentlyDate = moment(new Date(), 'DD.MM');
        var startDate = currentlyDate.subtract(30, "days");
        var defaultDays = [];

        for (let i = 30; i > 0; i--) {

            var oneDay = {
                x: startDate.format('DD.MM'),
                y: 0
            };

            startDate = startDate.add(1, "days");
            defaultDays.push(oneDay);
        }

        return defaultDays;
    }

    render() {
        console.log(" * * * КОРЕНЬ * * * ");
        console.log(this.state.charts);
        return (
            <div id="maskComponent">
                {!this.state.loading ? <Loader /> : null}
                <div className="sidebar" id="sidebar">
                    <Navigation activeItem={7717} />
                </div>
                <div className="panel" id="panel">
                    <MonthWorkspace
                        charts={this.state.charts}
                        defaultChart={this.state.defaultChart}
                        strainData={this.state.strainData}
                        startDate={this.state.startDate}
                        finishDate={this.state.finishDate}
                    />
                </div>                        
            </div>
        );
    }
}

export default Month;
