import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircle as fasColor} from '@fortawesome/free-solid-svg-icons'
import '../css/main.css';

class ReportsRowTable extends React.Component {
    constructor(props) {
        super(props);
        this.toggleChartClick = this.toggleChartClick.bind(this);

        this.state = {
            charts: props.chartReports
        };
    }

    toggleChartClick(event) {
        var presentButton = event.currentTarget;
        var presentButtonType = presentButton.getAttribute('data-type');
        this.props.toggleChart(presentButtonType);
        if (presentButton.classList.contains('table__row--active')) {
            presentButton.classList.remove('table__row--active');
            presentButton.classList.add('table__row--disabled');
        } else {
            presentButton.classList.remove('table__row--disabled');
            presentButton.classList.add('table__row--active');
        }
    }

    render() {
        var rowActivity = this.props.active ? 'table__row--body table__row--active' : 'table__row--body table__row--disabled';        
        return (
            <tr className={rowActivity} onClick={this.toggleChartClick} data-id={this.props.id} data-type={this.props.type}>
                <td className="table__cell table__cell--body"><FontAwesomeIcon icon={fasColor} color={this.props.color} /></td>
                <td className="table__cell table__cell--body left">{this.props.name}</td>
                <td className="table__cell table__cell--body">{this.props.count}</td>
            </tr>
        );
    }
}

export default ReportsRowTable;
