import React from 'react';
import DayTable from './React.day.table';
import DayChart from './React.day.chart';
import Userbar from './React.userbar';
import 'react-vis/dist/style.css';
import '../css/main.css';

class DayWorkspace extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            charts: props.chartTayble,
            rows: props.chartDay,
            changedRows: props.chartDay
        };
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
            charts: nextProps.chartReports,
            changedCharts: nextProps.chartReports
        });
    }

    findCharts = (foundCharts) => {
        this.setState({
            changedCharts: foundCharts
        });
    };

    render() {
        return (
            <div>
                <Userbar presentRows={this.state.rows} find={this.findCharts} />
                <div className="chart">
                    <h2>Суточный мониторинг нагрузки на систему</h2>
                    <DayChart chartDay={this.state.charts} />
                </div>
                <div className="data">
                    <h2>Показатели активности по часам</h2>
                    <DayTable rows={this.state.changedRows} />
                </div>
            </div>
        );
    }
}

export default DayWorkspace;
