import React from 'react';
import Navigation from './React.nav';
import Loader from './React.loader';
import moment from 'moment'
import ReportsWorkspace from './React.reports.workspace';
import '../css/main.css';

const COLORS = [
    '#4461c2',
    '#16a65e',
    '#85d357',
    '#ffdc45',
    '#ff4716',
    '#6ab4ec',
    '#b63dbc',
    '#fdaa19',
    '#fcacfd',
    '#8e8cd8'
];

const urlLogs = 'http://localhost:3000/logs';

class Reports extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            chartReports: [],
            startDate: '',
            finishDate: '',
            loading: false,
            settingPeriod: moment(new Date()).format('MM.YYYY'),  
        };
    }

    componentWillMount() {
        this.loadData(this.state.settingPeriod);
    }

    loadData(period) {
        console.log("Загружаемый период:");
        console.log(period);
        fetch(urlLogs + '?period=' + period)
            .then(function(response) {
                console.log(response);
                return response.json();
            })
            .then(result => this.parseData(result))
            .catch(e => console.log(e));
    }

    parseData(usersLogs) {
        console.log('Пришедшие логи:');
        console.log(usersLogs);
        var allLogs = this.getAllLogs(usersLogs);
        var listReports = this.getReports(allLogs);
        var chartReports = this.getDataReports(listReports, allLogs);
        console.log('Сформированные графики:');
        console.log(chartReports);
        var finishDate = moment(new Date(), 'DD.MM').subtract(1, "days");
        var startDate = moment(new Date(), 'DD.MM').subtract(15, "days");
        var defaultChart = this.getDefaultChart();

        this.setState({
            chartReports: chartReports,
            defaultChart: defaultChart,
            startDate: startDate.format('DD.MM'),
            finishDate: finishDate.format('DD.MM'),
            loading: true
        });
    }

    setDate = (changedPeriod) => {
        this.setState({
            loading: false
        });

        this.loadData(changedPeriod);
    }





    sortHoursChart(userLogs) {
        var dayData = [];

        var startHour = moment('00:00', 'HH:mm');
        var finishHour = moment('01:00', 'HH:mm');

        for (let i = 0; i < 24; i++) {
            var hourData = {
                x: startHour.format('HH:mm'),
                y: 0
            }

            for (let j = 0; j < userLogs.length; j++) {
                var visitTime = moment(userLogs[j].time.slice(6,11), 'HH:mm');
                if (visitTime.isBetween(startHour, finishHour, 'hours', '[)')) {
                    hourData.y++;
                }
            }

            dayData.push(hourData);
            startHour = startHour.add(1, "hours");
            finishHour = finishHour.add(1, "hours");
        }

        return dayData;
    }

    getAllLogs(userLogs) {
        var allLogs = [];

        for (let i = 0; i < userLogs.length; i++) {
            var logItems = userLogs[i].logItems;
            for (let j = 0; j < logItems.length; j++) {
                allLogs.push(logItems[j]);
            }
        }

        return allLogs;
    }

    getReports(allLogs) {
        var reports = [];

        for (let i = 0; i < allLogs.length; i++) {
            var presentLog = allLogs[i];
            var havePresentLog = false;
            for (let j = 0; j < reports.length; j++) {
                if (presentLog.title === reports[j].name) {
                    havePresentLog = true;
                    reports[j].count++;
                }
            }
            if (!havePresentLog) {
                var addLogItem = {
                    name: presentLog.title,
                    color: COLORS[reports.length % 10],
                    link: presentLog.link,
                    display: true,
                    count: 1
                }

                reports.push(addLogItem);
            }
        }

        return reports;
    }

    getDataReports(listReports, allLogs) {

        for (let i = 0; i < listReports.length; i++) {
            var presentReport = listReports[i];
            var currentlyDate = moment(new Date(), 'DD.MM');
            var startDate = currentlyDate.subtract(14, "days");

            presentReport.monthData = [];

            for (let j = 14; j > 0; j--) {
                var visitsOnDay = 0;
                for (let z = 0; z < allLogs.length; z++) {

                    if ((allLogs[z].date == startDate.format('DD.MM')) && (allLogs[z].title === presentReport.name)) {
                        visitsOnDay++;
                    }
                }

                var oneDayValue = {
                    x: startDate.format('DD.MM'),
                    y: visitsOnDay
                };

                startDate = startDate.add(1, "days");
                presentReport.monthData.push(oneDayValue);
            }
        }

        return listReports;
    }

    getDefaultChart() {
        var currentlyDate = moment(new Date(), 'DD.MM');
        var startDate = currentlyDate.subtract(15, "days");
        var defaultDays = [];

        for (let i = 15; i > 0; i--) {

            var oneDay = {
                x: startDate.format('DD.MM'),
                y: 0
            };

            startDate = startDate.add(1, "days");
            defaultDays.push(oneDay);
        }

        return defaultDays;
    }

    render() {
        return (
            <div id="maskComponent">
                {!this.state.loading ? <Loader /> : null}
                <div className="sidebar" id="sidebar">
                    <Navigation activeItem={7718} />
                </div>
                <div className="panel" id="panel">
                    <ReportsWorkspace
                        chartReports={this.state.chartReports}
                        startDate={this.state.startDate}
                        finishDate={this.state.finishDate}
                        defaultChart={this.state.defaultChart}
                        settingPeriod={this.state.settingPeriod}
                    />
                </div>                        
            </div>
        );
    }
}

export default Reports;
