import React from 'react';
import '../css/main.css';

function FindUsers(props) {
    function handleSearch(event) {
        var searchQuery = event.target.value.toLowerCase();
        var foundUsers = props.presentRows.filter(function(el) {
            var searchValue1 = el.userName.toLowerCase();
            var searchValue2 = el.userCompany.toLowerCase();
            return ((searchValue1.indexOf(searchQuery) !== -1) || (searchValue2.indexOf(searchQuery) !== -1));
        });
        props.find(foundUsers);
    }

    return (
        <div className="filter__elements">
            <input type="text" className="filter__finder" onChange={handleSearch} placeholder="Поиск пользователя по имени, организации и подразделению..." />
        </div>
    );
}

export default FindUsers;
