import React from 'react';
import ModeTable from './React.mode.table';
import ModeChart from './React.mode.chart';
import 'react-vis/dist/style.css';
import '../css/main.css';

class MonthWorkspace extends React.Component {
    constructor(props) {
        super(props);
        this.toggleChart = this.toggleChart.bind(this);

        this.state = {
            curPeriod: []
        };
    }

    render() {
        var curMonth = moment(new Date()).format('MM');
        var curYear = moment(new Date()).format('YYYY');
        var defaultPeriod;
        switch(curMonth) {
            case '01': 
                defaultPeriod = 'Январь ' + curYear;
                break;
            case '02': 
                defaultPeriod = 'Февраль ' + curYear;
                break;
            case '03': 
                defaultPeriod = 'Март ' + curYear;
                break;
            case '04': 
                defaultPeriod = 'Апрель ' + curYear;
                break;
            case '05': 
                defaultPeriod = 'Май ' + curYear;
                break;
            case '06': 
                defaultPeriod = 'Июнь ' + curYear;
                break;
            case '07': 
                defaultPeriod = 'Июль ' + curYear;
                break;
            case '08': 
                defaultPeriod = 'Август ' + curYear;
                break;
            case '09': 
                defaultPeriod = 'Сентябрь ' + curYear;
                break;
            case '10': 
                defaultPeriod = 'Октябрь ' + curYear;
                break;
            case '11': 
                defaultPeriod = 'Ноябрь ' + curYear;
                break;
            case '12': 
                defaultPeriod = 'Декабрь ' + curYear;
                break;
            default:
                defaultPeriod = 'Январь ' + curYear;
                break;
        };

        return (
            <div>                
                <div className="chart">
                    <h2>Динамика нарушений режима за</h2>
                    <select className="mode__period" onChange={this.setPeriod} defaultValue={defaultPeriod}>
                        <option value="07.2019">Июль 2019</option>
                        <option value="06.2019">Июнь 2019</option>
                        <option value="05.2019">Май 2019</option>
                        <option value="04.2019">Апрель 2019</option>
                        <option value="03.2019">Март 2019</option>
                        <option value="02.2019">Февраль 2019</option>
                        <option value="01.2019">Январь 2019</option>
                        <option value="12.2018">Декабрь 2018</option>
                        <option value="11.2018">Ноябрь 2018</option>
                    </select>
                    <ModeChart
                        charts={this.props.charts}
                        defaultChart={this.props.defaultChart}
                    />
                </div>
                <div className="data">
                    <h2>Таблица показателей активности</h2>
                    <ModeTable rows={this.props.strainData} />
                </div>
            </div>
        );
    }
}

export default MonthWorkspace;
