import React from 'react';
import dataLoad from '../models/dataLoad';
import Navigation from './React.nav';
import Loader from './React.loader';
import moment from 'moment'
import DayWorkspace from './React.day.workspace';
import '../css/main.css';

const urlLogs = 'http://localhost:3000/logs';

class Day extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            chartDay: [],
            tableDay: [],
            settingDate: moment(new Date()).format('DD.MM.YYYY'), 
            loading: false
        };
    }

    componentWillMount() {
        this.loadData(this.state.settingDate);
    }

    setDate = (changedDate) => {
        var formatedDate = moment(changedDate, 'YYYY-MM-DD').format('DD.MM.YYYY');
        this.loadData(formatedDate);

        this.setState({

        });
    }

    loadData(date) {

        var usersLogs;

        dataLoad(urlLogs + '?date=' + date).then(logs => {
            usersLogs = JSON.parse(logs);

            var chartHours = this.sortHoursChart(usersLogs);
            var tableHours = this.sortHoursTable(usersLogs);

            console.log(chartHours);

            this.setState({
                chartDay: chartHours,
                tableDay: tableHours,
                settingDate: date,
                loading: true
            });

        }).catch(function(logs) {
            console.log('error ' + logs);
        })
    }

    sortHoursChart(userLogs) {
        var dayData = [];

        var startHour = moment('00:00', 'HH:mm');
        var finishHour = moment('01:00', 'HH:mm');

        for (let i = 0; i < 24; i++) {
            var hourData = {
                x: startHour.format('HH:mm'),
                y: 0
            }

            for (let j = 0; j < userLogs.length; j++) {
                var visitTime = moment(userLogs[j].time.slice(6,11), 'HH:mm');
                if (visitTime.isBetween(startHour, finishHour, 'hours', '[)')) {
                    hourData.y++;
                }
            }

            dayData.push(hourData);
            startHour = startHour.add(1, "hours");
            finishHour = finishHour.add(1, "hours");
        }

        return dayData;
    }

    sortHoursTable(userLogs) {
        var dayData = [];

        var startHour = moment('00:00', 'HH:mm');
        var finishHour = moment('01:00', 'HH:mm');

        for (let i = 0; i < 24; i++) {
            var hourData = {
                start: startHour.format('HH:mm'),
                finish: finishHour.format('HH:mm'),
                visits: 0
            }

            for (let j = 0; j < userLogs.length; j++) {
                var visitTime = moment(userLogs[j].time.slice(6,11), 'HH:mm');
                if (visitTime.isBetween(startHour, finishHour, 'hours', '[)')) {
                    hourData.visits++;
                }
            }

            dayData.push(hourData);
            startHour = startHour.add(1, "hours");
            finishHour = finishHour.add(1, "hours");
        }

        return dayData;
    }

    render() {
        console.log('Текущая дата: ');
        console.log(this.state.settingDate);
        return (
            <div id="maskComponent">
                {!this.state.loading ? <Loader /> : null}
                <div className="sidebar" id="sidebar">
                    <Navigation activeItem={7711} />
                </div>
                <div className="panel" id="panel">
                    <DayWorkspace
                        chartDay={this.state.chartDay}
                        tableDay={this.state.tableDay}
                        setDate={this.setDate}
                    />
                </div>                        
            </div>
        );
    }
}

export default Day;
