import React from 'react';
import moment from 'moment';
import Userbar from './React.userbar';
import VisitsTable from './React.visits.table';
import FindVisits from './React.visits.find';
import '../css/main.css';

class VisitsWorkspace extends React.Component {
    constructor(props) {
        super(props);
        this.setPeriod = this.setPeriod.bind(this);
        this.state = {
            rows: props.visitsItems,
            changedRows: props.visitsItems
        };
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
            rows: nextProps.visitsItems,
            changedRows: nextProps.visitsItems
        })
    }

    setPeriod(event) {
        var newPeriod = event.target.value;
        this.props.setPeriod(newPeriod);
    }

    findRows = (foundRows) => {
        this.setState({
            changedRows: foundRows
        });
    };

    render() {
        console.log('ФУНКЦИЯ');
        console.log(this.props.setPeriod);
        var curMonth = moment(new Date()).format('MM');
        var curYear = moment(new Date()).format('YYYY');
        var defaultPeriod;
        switch(curMonth) {
            case '01': 
                defaultPeriod = 'Январь ' + curYear;
                break;
            case '02': 
                defaultPeriod = 'Февраль ' + curYear;
                break;
            case '03': 
                defaultPeriod = 'Март ' + curYear;
                break;
            case '04': 
                defaultPeriod = 'Апрель ' + curYear;
                break;
            case '05': 
                defaultPeriod = 'Май ' + curYear;
                break;
            case '06': 
                defaultPeriod = 'Июнь ' + curYear;
                break;
            case '07': 
                defaultPeriod = 'Июль ' + curYear;
                break;
            case '08': 
                defaultPeriod = 'Август ' + curYear;
                break;
            case '09': 
                defaultPeriod = 'Сентябрь ' + curYear;
                break;
            case '10': 
                defaultPeriod = 'Октябрь ' + curYear;
                break;
            case '11': 
                defaultPeriod = 'Ноябрь ' + curYear;
                break;
            case '12': 
                defaultPeriod = 'Декабрь ' + curYear;
                break;
            default:
                defaultPeriod = 'Январь ' + curYear;
                break;
        };

        return (
            <div>
                <Userbar>
                    <FindVisits presentRows={this.state.rows} find={this.findRows} />
                    <select className="userbar__period" onChange={this.setPeriod} defaultValue={defaultPeriod}>
                        <option value="07.2019">Июль 2019</option>
                        <option value="06.2019">Июнь 2019</option>
                        <option value="05.2019">Май 2019</option>
                        <option value="04.2019">Апрель 2019</option>
                        <option value="03.2019">Март 2019</option>
                        <option value="02.2019">Февраль 2019</option>
                        <option value="01.2019">Январь 2019</option>
                        <option value="12.2018">Декабрь 2018</option>
                        <option value="11.2018">Ноябрь 2018</option>
                    </select>
                </Userbar>

                <div className="title">
                    <h1>Количество обращений</h1>
                    <p>по пользователям системы САРМ ПП</p>
                </div>

                <div className="data">
                    <VisitsTable rows={this.state.changedRows} />
                </div>
            </div>
        );
    }
}

export default VisitsWorkspace;
